F1a_plot <- F1a_sf %>% dplyr::select(prop.pop, prop.F1a, geometry) %>% st_as_sf(crs = 4326)

## Check for NULL values
if (any(is.null(F1a_plot$prop.pop)) || any(is.null(F1a_plot$prop.F1a))) {
  stop("There are NULL values in the data.")
}

