locations <- SEA1_sf
locations_coords <- st_coordinates(st_centroid(SEA1_sf)) %>%
  data.frame(stringsAsFactors = FALSE) %>%
  mutate(ID = locations$location)

table(dat_ethnic_SEA$location %in% locations$location)

