    mapping=aes(fill=Ethnicity),
    width=0.0003,
    offset=0.03
  ) +
  scale_fill_manual(
    name="Ethnicity",
    values=Ethniccolors,
    guide=guide_legend(keywidth=2, 
                       keyheight=3, 
                       ncol = 1,
                       order = 4,
                       override.aes=list(size=1,alpha=1),
