p <- p %<+% metadata

p1 <- p +
  geom_tippoint(aes(color=Country),
                size=5) + 
  scale_color_manual(values=cols, 
                     guide=guide_legend(keywidth=2,
                                        keyheight=3,
                                        order=2,
                                        override.aes=list(size=15,alpha=1))) + 
  theme(legend.position="right",
        legend.background=element_rect(fill=NA),
        legend.title=element_text(size=30, face="bold"),
        legend.text=element_text(size=24),
        legend.key.size = unit(30, "cm"),
        legend.spacing.y = unit(2, "cm")) + 
  new_scale_colour()

p4 <-p1 +
  geom_fruit(
    geom=geom_tile,
    mapping=aes(fill=`Language family`),
    width=0.0003,
    offset=0.03
  ) +
  scale_fill_manual(
    name="Language",
    values=Languagecolors,
    guide=guide_legend(keywidth=3, 
                       keyheight=3, 
                       order=1,
                       override.aes=list(size=10,alpha=1))
  ) +
  theme(legend.background=element_rect(fill=NA),
        legend.title=element_text(size=30, face="bold"), 
        legend.text=element_text(size=24),
        legend.key.size = unit(30, "cm"),
        legend.spacing.y = unit(2, "cm")) + 
  new_scale_colour()

p6 <- p4 +
  new_scale_fill() +
  geom_fruit(
    geom=geom_tile,
    mapping=aes(fill=Ethnicity),
    width=0.0003,
    offset=0.03
  ) +
  scale_fill_manual(
    name="Ethnicity",
    values=Ethniccolors,
    guide=guide_legend(keywidth=2, 
                       keyheight=3, 
                       ncol = 1,
                       order = 4,
                       override.aes=list(size=1,alpha=1),
                       title.position="top")
  ) +
  theme(legend.background=element_rect(fill=NA),
        legend.title=element_text(size=36, face="bold"), 
        legend.text=element_text(size=22),
        legend.key.size = unit(10, "cm"),
        legend.spacing.y = unit(2, "cm")) + 
  new_scale_colour()

p7 <- p6 +
  new_scale_fill() +
  geom_fruit(geom=geom_star,
             mapping=aes(fill=haplogroup1),
             size=30,
             starstroke=0,
             pwidth=0.2,
             inherit.aes = FALSE,
             grid.params=list(linetype=3, size=0.2)) +
  scale_fill_manual(
    name="Haplogroup",
    values = Haplocolors,
    guide=guide_legend(keywidth=2, 
                       keyheight=3, 
                       order=3,
                       nrow = 2, byrow = TRUE,
                       override.aes=list(size=10)),
    na.translate=FALSE) +
  scale_starshape_discrete(guide="none") +
  ggtitle("Malaysia") + 
  theme(plot.title = element_text(size = 100, face = "bold")) +
  theme(legend.background=element_rect(fill=NA),
        legend.title=element_text(size=40, face="bold"), 
        legend.text=element_text(size=30),
        legend.key.size = unit(30, "cm"),
        legend.spacing.y = unit(2, "cm"),
        legend.box = "vertical",
        legend.direction = "vertical",
        legend.position = "right") + 
  new_scale_colour()
p7

