D4_plot <- D4_sf %>% dplyr::select(prop.pop, prop.D4, geometry) %>% st_as_sf(crs = 4326)

## ordinary kriging
D4.ok <- gstat::krige(prop.pop ~ 1, D4_plot, SEA1_sf)

## Convert the result back to sf object
D4Krige_sf <- st_as_sf(D4.ok)

ggplot() +
  geom_sf(data = SEA1_sf, fill = "white", alpha = 0.001) +
  geom_sf(data = D4Krige_sf, aes(fill = var1.pred), lwd = 0) +
  annotate(geom = "text", x = 130, y = 23, color = "red", size = 18, label = paste0(round(D4_plot$prop.D4[1], 1), "% population")) +
  scale_fill_viridis("Percent (%)", direction = -1, option = "viridis") +
  theme_bw() +
  theme(
    text = element_text(size = 45),
    axis.text.x = element_text(size = 30),
    axis.text.y = element_text(size = 30),
    legend.text = element_text(size = 30),
    legend.key.size = unit(2, "cm")
  ) +
  ggtitle("Haplogroup D4")

ggsave(filename = file.path("figures", "S19_Fig_2.png"), width = 42, height = 33)

